import React, { useState } from 'react';
import { Edit3, Save, X } from 'lucide-react';

const PropertyEditor = ({ data, onDataUpdate }) => {
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({});

  const handleEdit = (property) => {
    setEditingProperty(property['MLS #']);
    setEditForm({
      'Status Contractual': property['Status Contractual'] || '',
      'Long Text': property['Long Text'] || '',
      'Upgrades': property['Upgrades'] || '',
      'Parking': property['Parking'] || '',
      'BR up1': property['BR up1'] || '',
      'FB up1': property['FB up1'] || '',
      'Main Level BR': property['Main Level BR'] || '',
      'Main Level Full Bath': property['Main Level Full Bath'] || '',
    });
  };

  const handleSave = () => {
    const updatedData = data.map(property => {
      if (property['MLS #'] === editingProperty) {
        return { ...property, ...editForm };
      }
      return property;
    });
    onDataUpdate(updatedData);
    setEditingProperty(null);
    setEditForm({});
  };

  const handleCancel = () => {
    setEditingProperty(null);
    setEditForm({});
  };

  const handleInputChange = (field, value) => {
    setEditForm(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Property Details Editor</h2>
        <p className="text-sm text-gray-600">Click edit to modify property details</p>
      </div>

      <div className="table-container">
        <table className="table">
          <thead>
            <tr>
              <th className="table-header">Address</th>
              <th className="table-header">MLS #</th>
              <th className="table-header">Status Contractual</th>
              <th className="table-header">Long Text</th>
              <th className="table-header">Upgrades</th>
              <th className="table-header">Parking</th>
              <th className="table-header">BR up1</th>
              <th className="table-header">FB up1</th>
              <th className="table-header">Main Level BR</th>
              <th className="table-header">Main Level Full Bath</th>
              <th className="table-header">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {data.map((property, index) => (
              <tr key={index} className="table-row">
                <td className="table-cell font-medium">{property['Address']}</td>
                <td className="table-cell">{property['MLS #']}</td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="text"
                      value={editForm['Status Contractual']}
                      onChange={(e) => handleInputChange('Status Contractual', e.target.value)}
                      className="input-field text-sm"
                    />
                  ) : (
                    <span>{property['Status Contractual'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <textarea
                      value={editForm['Long Text']}
                      onChange={(e) => handleInputChange('Long Text', e.target.value)}
                      className="input-field text-sm"
                      rows="2"
                    />
                  ) : (
                    <span>{property['Long Text'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <textarea
                      value={editForm['Upgrades']}
                      onChange={(e) => handleInputChange('Upgrades', e.target.value)}
                      className="input-field text-sm"
                      rows="2"
                    />
                  ) : (
                    <span>{property['Upgrades'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="text"
                      value={editForm['Parking']}
                      onChange={(e) => handleInputChange('Parking', e.target.value)}
                      className="input-field text-sm"
                    />
                  ) : (
                    <span>{property['Parking'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="number"
                      value={editForm['BR up1']}
                      onChange={(e) => handleInputChange('BR up1', e.target.value)}
                      className="input-field text-sm"
                      min="0"
                    />
                  ) : (
                    <span>{property['BR up1'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="number"
                      value={editForm['FB up1']}
                      onChange={(e) => handleInputChange('FB up1', e.target.value)}
                      className="input-field text-sm"
                      min="0"
                      step="0.5"
                    />
                  ) : (
                    <span>{property['FB up1'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="number"
                      value={editForm['Main Level BR']}
                      onChange={(e) => handleInputChange('Main Level BR', e.target.value)}
                      className="input-field text-sm"
                      min="0"
                    />
                  ) : (
                    <span>{property['Main Level BR'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <input
                      type="number"
                      value={editForm['Main Level Full Bath']}
                      onChange={(e) => handleInputChange('Main Level Full Bath', e.target.value)}
                      className="input-field text-sm"
                      min="0"
                      step="0.5"
                    />
                  ) : (
                    <span>{property['Main Level Full Bath'] || 'Not set'}</span>
                  )}
                </td>

                <td className="table-cell">
                  {editingProperty === property['MLS #'] ? (
                    <div className="flex space-x-2">
                      <button onClick={handleSave} className="p-1 text-green-600 hover:text-green-700">
                        <Save className="w-4 h-4" />
                      </button>
                      <button onClick={handleCancel} className="p-1 text-red-600 hover:text-red-700">
                        <X className="w-4 h-4" />
                      </button>
                    </div>
                  ) : (
                    <button onClick={() => handleEdit(property)} className="p-1 text-blue-600 hover:text-blue-700">
                      <Edit3 className="w-4 h-4" />
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PropertyEditor;
